import axios from 'axios'
import React, {useEffect, useState} from 'react'
import ProductoIndividual from './ProductoIndividual'


function ProductosDisponibles(){

    const[dataproductos, setdataproductos]=useState([])

    //const ProDispo = [...new Set(dataproductos.map((Val) => Val.))];


    useEffect(() => {

        axios.get('api/producto/obtenerproducto').then(res => {
            console.log(res.data)
            setdataproductos(res.data)
        }).catch(err => {
            console.log(err)
        })
       
    },[])

    //Mapera listaproductos en objeto productos
    const listaproductos = dataproductos.map(producto => {
        return(
            <div>
                <ProductoIndividual producto={producto}/>
            </div>
        )
    })


    const filterItem = (curcat) => {
        const newItem = dataproductos.filter((newVal) => {
          return newVal.cantidad === curcat;
        });
        setItem(newItem);
      };

      return (
        <>
          <div className="container-fluid">
            <div className="row">
              <h1 className="col-12 text-center my-3 fw-bold">Food Filtering App</h1>
              <Buttons
                filterItem={filterItem}
                setItem={setItem}
                //menuItems={menuItems}
              />
              <Card item={item} />
            </div>
          </div>
        </>
      );

    

}
export default ProductosDisponibles
